mapper.select:
select log_value, create_time from iot_device_log

     and device_id = #{deviceId} 
     and identity = #{identity} 
     and is_monitor=1
     and create_time between #{beginTime} and #{endTime}

order by create_time desc
limit #{total}

select alllog.*,r.room_id,ifnull(r.name,'未分配') as room_name from (select log.log_id, log.log_type, log.log_value, log.log_text, log.device_id, d.device_name,log.serial_number , log.identity, log.create_by, log.is_monitor,log.mode, log.user_id, log.user_name, log.tenant_id, log.tenant_name, log.create_time, log.remark , d.img_url, d.device_model_type, d.belong_room_id , u.nick_name, u.phonenumber,f.family_id,f.name as family_name from iot_device d ,iot_family f, iot_device_log log left join sys_user u on log.user_id=u.user_id
d.device_id=log.device_id and f.family_id=d.belong_family_id and log.is_monitor !=1 and f.family_id in
#{fId}
and log.device_id = #{deviceId} and f.family_id = #{familyId} and log.log_type = #{logType} and log.identity like concat('%', #{identity}, '%') and d.device_name like concat('%', #{deviceName}, '%')
) alllog left join iot_room r on r.room_id=alllog.belong_room_id
1 = 1 and r.room_id = #{roomId}
order by alllog.create_time desc

SELECT
(select count(log_id)
    from iot_device_log

             and log_type=1
             and user_id = #{userId}
             and tenant_id = #{tenantId}

) as propertyCount,
(select count(log_id)
    from iot_device_log

         and log_type=2
         and user_id = #{userId}
         and tenant_id = #{tenantId}

) as functionCount,
(select count(log_id)
    from iot_device_log

         and log_type=3
         and user_id = #{userId}
         and tenant_id = #{tenantId}

) as eventCount,
(select count(log_id)
    from iot_device_log

         and log_type=1 and is_monitor=1
         and user_id = #{userId}
         and tenant_id = #{tenantId}

) as monitorCount
limit 1

mapper.update:
update iot_device_log
log_type = #{logType}, log_value = #{logValue}, log_text = #{logText}, device_id = #{deviceId}, device_name = #{deviceName}, serial_number = #{serialNumber}, identity = #{identity}, create_by = #{createBy}, is_monitor = #{isMonitor}, mode = #{mode}, create_time = #{createTime}, remark = #{remark}, user_id = #{userId}, user_name = #{userName}, tenant_id = #{tenantId}, tenant_name = #{tenantName},
where log_id = #{logId}
