select d.device_id, d.device_name, d.product_id, d.product_name, d.user_id, d.user_name, d.tenant_id, d.tenant_name,
d.serial_number, d.firmware_version, d.status,d.is_shadow ,d.location_way,d.active_time, d.img_url,a.device_id as auth_device_id
from iot_device d
left join iot_product_authorize a on a.device_id=d.device_id

     and ISNULL(a.device_id)
     and d.device_name like concat('%', #{deviceName}, '%')
     and d.product_id = #{productId}
     and d.product_name like concat('%', #{productName}, '%')
     and d.user_id = #{userId}
     and d.user_name like concat('%', #{userName}, '%')
     and d.tenant_id = #{tenantId}
     and d.tenant_name like concat('%', #{tenantName}, '%')
     and d.serial_number = #{serialNumber}
     and d.status = #{status}
     and d.active_time between #{params.beginActiveTime} and #{params.endActiveTime}

order by d.create_time desc

select d.device_id, d.device_name, d.product_name, d.user_name, d.serial_number, d.firmware_version, d.status,d.rssi,d.is_shadow ,
d.location_way, d.active_time,d.network_address,d.longitude,d.latitude,max(u.is_owner) as is_owner
from iot_device d
inner join iot_device_user u on u.device_id = d.device_id

     and u.user_id = #{userId}
     and d.product_id = #{productId}
     and d.device_name like concat('%', #{deviceName}, '%')
     and d.product_name like concat('%', #{productName}, '%')
     and d.serial_number = #{serialNumber}
     and d.status = #{status}
     and d.network_address like concat('%', #{networkAddress}, '%')
     and d.active_time between #{params.beginActiveTime} and #{params.endActiveTime}

group by d.device_id,d.user_id
order by d.create_time desc

select d.device_id, d.device_name, d.product_name, d.user_name, d.serial_number, d.firmware_version, d.status,d.rssi,d.is_shadow ,
       d.location_way, d.active_time,d.network_address,d.longitude,d.latitude,max(u.is_owner) as is_owner
from iot_device d
inner join iot_device_user u on u.device_id = d.device_id

     and u.user_id = #{userId}
     and d.tenant_id = #{tenantId}
     and d.product_id = #{productId}

group by d.device_id,d.user_id

select d.device_id, d.device_name, d.product_id, d.product_name,
    d.user_id, d.user_name, d.tenant_id, d.tenant_name, d.serial_number,
    d.firmware_version, d.status,d.rssi,d.is_shadow ,d.location_way,
    d.things_model_value, d.active_time,d.img_url,max(u.is_owner) as is_owner
from iot_device d
inner join iot_device_user u on u.device_id = d.device_id
 left join iot_device_group g on g.device_id=d.device_id 

     and g.group_id = #{groupId}
     and u.user_id = #{userId}
     and d.tenant_id = #{tenantId}
     and d.device_name like concat('%', #{deviceName}, '%')
     and d.product_id = #{productId}
     and d.product_name like concat('%', #{productName}, '%')
     and d.user_name like concat('%', #{userName}, '%')
     and d.tenant_name like concat('%', #{tenantName}, '%')
     and d.serial_number = #{serialNumber}
     and d.status = #{status}
     and d.active_time between #{params.beginActiveTime} and #{params.endActiveTime}

group by d.device_id,d.user_id
order by d.create_time desc

select d.device_id, d.device_name, d.product_id, d.product_name,
    d.user_id, d.user_name, d.tenant_id, d.tenant_name, d.serial_number,
    d.firmware_version, d.status,d.rssi,d.is_shadow ,d.location_way,
    d.things_model_value, d.active_time,d.img_url,d.belong_family_id,
    f.position as network_address,f.belong_user_id,f.name as belong_family_name,
    u.nick_name as belong_user_name
from iot_device d
left join iot_family f on d.belong_family_id=f.family_id,
sys_user u

 	u.user_id = f.belong_user_id
     and d.tenant_id = #{tenantId}
     and d.device_name like concat('%', #{deviceName}, '%')
     and d.product_id = #{productId}
     and d.belong_family_id = #{belongFamilyId}
     and d.device_type = #{deviceType}
     and d.product_name like concat('%', #{productName}, '%')
     and d.user_name like concat('%', #{userName}, '%')
     and d.tenant_name like concat('%', #{tenantName}, '%')
     and d.serial_number = #{serialNumber}
     and d.status = #{status}
     and d.active_time between #{params.beginActiveTime} and #{params.endActiveTime}

group by d.device_id,d.user_id
order by d.create_time desc

select

    (select count(distinct d.device_id,d.user_id)
    from iot_device d
    inner join iot_device_user u on u.device_id = d.device_id

         and u.user_id = #{userId}
         and d.tenant_id = #{tenantId}

    ) as deviceCount,

   (


       select count(distinct product_id)
       from iot_device
       where user_id = #{userId}



        select count(product_id)
        from iot_product

             and tenant_id = #{tenantId} 


    ) as productCount,

   (select count(alert_log_id)
        from iot_alert_log

             and user_id = #{userId}
             and tenant_id = #{tenantId}

   ) as alertCount

mapper.update:
update iot_device
status = #{status}, network_address = #{networkAddress}, network_ip = #{networkIp}, longitude = #{longitude}, latitude = #{latitude}, active_time = #{activeTime},
where serial_number = #{serialNumber}

update iot_device
device_name = #{deviceName}, product_id = #{productId}, product_name = #{productName}, user_id = #{userId}, user_name = #{userName}, tenant_id = #{tenantId}, tenant_name = #{tenantName}, serial_number = #{serialNumber}, firmware_version = #{firmwareVersion}, status = #{status}, rssi = #{rssi}, is_shadow = #{isShadow}, location_way = #{locationWay}, things_model_value = #{thingsModelValue}, network_address = #{networkAddress}, network_ip = #{networkIp}, longitude = #{longitude}, latitude = #{latitude}, active_time = #{activeTime}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, img_url = #{imgUrl}, summary = #{summary},
where serial_number = #{serialNumber}

mapper.delete:
delete from iot_device_group

     and device_id = #{deviceId}
     and group_id in(select group_id from iot_group where user_id = #{userId})

mapper.select:
 select count(*) from iot_device d

		d.belong_family_id = #{familyId} and d.device_type != 3
      and d.belong_room_id = #{roomId}

select d.device_id, d.device_name, d.product_name, d.product_id,d.serial_number, d.status,d.things_model_value ,d.img_url,d.belong_family_id,d.belong_room_id,d.is_family_usual,d.family_gw_device_id,d.device_model_type ,ifnull(r.name,'未分配') as belong_room_name from iot_device d left join iot_room r on d.belong_room_id = r.room_id
d.belong_family_id = #{familyId} and d.device_type != 3 and d.belong_room_id = #{roomId}
order by d.create_time desc
